import React, { useState } from 'react';
import './App.css';
import HomePublic from './components/HomePublic';
import Login from './components/Login';
import Register from './components/Register';
import Autorizar from './components/Autorizar';

export default function App() {
  const [screen, setScreen] = useState('public'); // 'public' | 'auth' | 'login' | 'register'

  if (screen === 'login')    return <Login onCancel={() => setScreen('public')} />;
  if (screen === 'register') return <Register onCancel={() => setScreen('public')} />;
  if (screen === 'auth')     return (
    <Autorizar
      onLogin={() => setScreen('login')}
      onRegister={() => setScreen('register')}
    />
  );

  // Landing público
  return (
    <HomePublic
      onRequireAuth={() => setScreen('auth')}
      onGoLogin={() => setScreen('login')}
    />
  );
}
