import React, { useState } from 'react';
import './App.css';
import HomePublic from './components/HomePublic';
import Login from './components/Login';
import Register from './components/Register';
import Autorizar from './components/Autorizar';
import ListsView from './components/ListsView'; // opcional

export default function App() {
  // posibles pantallas:
  // 'public' (inicio), 'auth', 'login', 'register', 'listas'
  const [screen, setScreen] = useState('public');

  // Pantalla de inicio de sesión
  if (screen === 'login')
    return <Login onCancel={() => setScreen('public')} />;

  // Pantalla de registro
  if (screen === 'register')
    return <Register onCancel={() => setScreen('public')} />;

  // Pantalla de autorización con botones "Iniciar Sesión" y "Nuevo Usuario"
  if (screen === 'auth')
    return (
      <Autorizar
        onLogin={() => setScreen('login')}
        onRegister={() => setScreen('register')}
      />
    );

  // Pantalla de listas (si deseas agregarla al flujo)
  if (screen === 'listas')
    return (
      <ListsView
        onGoLogin={() => setScreen('login')}
        onGoPeliculas={() => setScreen('public')}
      />
    );

  // Pantalla pública por defecto
  return (
    <HomePublic
      onRequireAuth={() => setScreen('auth')}
      onGoLogin={() => setScreen('login')}
    />
  );
}
