import React, { useState } from 'react';
import './App.css';
import Autorizar from './components/Autorizar';
import Login from './components/Login';
import Register from './components/Register';

export default function App() {
  const [screen, setScreen] = useState('home'); // 'home' | 'login' | 'register'

  if (screen === 'login')    return <Login onCancel={() => setScreen('home')} />;
  if (screen === 'register') return <Register onCancel={() => setScreen('home')} />;

  return (
    <Autorizar
      onLogin={() => setScreen('login')}
      onRegister={() => setScreen('register')}
    />
  );
}
